#include <stdlib.h>

// Function to compare two integers for qsort in descending order
int compare(const void *a, const void *b) {
    int l = *(const int*)a;
    int r = *(const int*)b;
    return r - l; // For descending order
}

long long maxSpending(int** values, int valuesSize, int* valuesColSize) {
    int total = valuesSize * (*valuesColSize); // Calculate total number of items
    int* items = malloc(total * sizeof(int)); // Array to store all values flattened
    int k = 0; // Index for items array

    // Flatten the matrix into the items array
    for (int i = 0; i < valuesSize; i++) {
        for (int j = 0; j < valuesColSize[i]; j++) {
            items[k++] = values[i][j];
        }
    }

    // Sort the items array in non-increasing order
    qsort(items, total, sizeof(int), compare);

    long long maxMoneySpent = 0;
    // Calculate the maximum money spent
    for (int day = 1; day <= total; day++) {
        maxMoneySpent += (long long)items[day - 1] * day;
    }

    free(items); // Free the allocated memory
    return maxMoneySpent; // Return the calculated amount
}
