func maxSpending(values [][]int) int64 {
    type item struct {
        value int
        day   int
    }

    var heap []item

    push := func(value, day int) {
        heap = append(heap, item{value, day})
        i := len(heap) - 1
        for i > 0 {
            p := (i - 1) / 2 // parent
            if heap[p].value >= heap[i].value {
                break
            }
            heap[i], heap[p] = heap[p], heap[i]
            i = p
        }
    }

    pop := func() item {
        top := heap[0]
        last := heap[len(heap)-1]
        heap[0] = last
        heap = heap[:len(heap)-1]

        i := 0
        for {
            left := 2*i + 1
            right := 2*i + 2
            largest := i
            if left < len(heap) && heap[left].value > heap[largest].value {
                largest = left
            }
            if right < len(heap) && heap[right].value > heap[largest].value {
                largest = right
            }
            if largest == i {
                break
            }
            heap[i], heap[largest] = heap[largest], heap[i]
            i = largest
        }

        return top
    }

    for i, shop := range values {
        for j := range shop {
            push(shop[j], i)
        }
    }

    var result int64 = 0
    day := 1
    for len(heap) > 0 {
        top := pop()
        result += int64(top.value) * int64(day)
        day++
    }

    return result
}
