#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    vector<int> maximumSumQueries(vector<int>& nums1, vector<int>& nums2, vector<vector<int>>& queries) {
        int n = nums1.size();
        vector<int> ans(queries.size());
        vector<pair<int, int>> elements(n);

        for (int i = 0; i < n; ++i) {
            elements[i] = {max(nums1[i], nums2[i]), nums1[i] + nums2[i]}; // Pair of max value and sum
        }

        sort(elements.begin(), elements.end()); // Sort based on max value

        for (int i = 0; i < queries.size(); ++i) {
            int xi = queries[i][0], yi = queries[i][1], maxSum = -1;
            int requiredMax = max(xi, yi);

            // Binary search to find the first element that satisfies the condition
            int low = 0, high = n - 1, mid;
            while (low <= high) {
                mid = low + (high - low) / 2;
                if (elements[mid].first >= requiredMax) {
                    maxSum = max(maxSum, elements[mid].second);
                    high = mid - 1; // Try to find a better solution on the left
                } else {
                    low = mid + 1;
                }
            }

            // Linear search for elements that satisfy the condition
            for (int j = low; j < n; ++j) {
                if (elements[j].first >= requiredMax) {
                    maxSum = max(maxSum, elements[j].second);
                }
            }

            ans[i] = maxSum;
        }

        return ans;
    }
};
