#include <stdlib.h>

int cmp(const void *a, const void *b) {
    int *pairA = *(int**)a;
    int *pairB = *(int**)b;
    if (pairA[0] == pairB[0]) return pairB[1] - pairA[1];
    return pairB[0] - pairA[0];
}

/**
 * Note: The returned array must be malloced, assume caller calls free().
 */
int* maximumSumQueries(int* nums1, int nums1Size, int* nums2, int nums2Size, int** queries, int queriesSize, int* queriesColSize, int* returnSize) {
    *returnSize = queriesSize;
    int* answer = (int*)malloc(queriesSize * sizeof(int));
    int** pairs = (int**)malloc(nums1Size * sizeof(int*));
    for (int i = 0; i < nums1Size; i++) {
        pairs[i] = (int*)malloc(2 * sizeof(int));
        pairs[i][0] = nums1[i];
        pairs[i][1] = nums2[i];
    }

    qsort(pairs, nums1Size, sizeof(pairs[0]), cmp);

    for (int i = 0; i < queriesSize; i++) {
        int maxSum = -1;
        for (int j = 0; j < nums1Size; j++) {
            if (pairs[j][0] >= queries[i][0] && pairs[j][1] >= queries[i][1]) {
                maxSum = pairs[j][0] + pairs[j][1];
                break; // Found the maximum possible sum for this query
            }
        }
        answer[i] = maxSum;
    }

    // Free the allocated memory for pairs
    for (int i = 0; i < nums1Size; i++) {
        free(pairs[i]);
    }
    free(pairs);

    return answer;
}
