func maximumSumQueries(nums1 []int, nums2 []int, queries [][]int) []int {
    n := len(nums1)
    sums := make([]int, n)
    for i := 0; i < n; i++ {
        sums[i] = nums1[i] + nums2[i]
    }

    type queryWithIndex struct {
        x, y, index int
    }
    queriesWithIndex := make([]queryWithIndex, len(queries))
    for i, q := range queries {
        queriesWithIndex[i] = queryWithIndex{q[0], q[1], i}
    }

    sort.Slice(queriesWithIndex, func(i, j int) bool {
        if queriesWithIndex[i].x == queriesWithIndex[j].x {
            return queriesWithIndex[i].y < queriesWithIndex[j].y
        }
        return queriesWithIndex[i].x < queriesWithIndex[j].x
    })

    maxSums := make([]int, len(queries))
    for i := range maxSums {
        maxSums[i] = -1
    }

    maxX, maxY := queriesWithIndex[len(queriesWithIndex)-1].x, queriesWithIndex[len(queriesWithIndex)-1].y
    maxXSum, maxYSum := make(map[int]int), make(map[int]int)

    for _, q := range queriesWithIndex {
        maxX = max(maxX, q.x)
        maxY = max(maxY, q.y)
        maxSumX, maxSumY := -1, -1
        if sum, exists := maxXSum[q.x]; exists {
            maxSumX = sum
        } else {
            for i := 0; i < n; i++ {
                if nums1[i] >= q.x && sums[i] > maxSumX {
                    maxSumX = sums[i]
                }
            }
            maxXSum[q.x] = maxSumX
        }
        if sum, exists := maxYSum[q.y]; exists {
            maxSumY = sum
        } else {
            for i := 0; i < n; i++ {
                if nums2[i] >= q.y && sums[i] > maxSumY {
                    maxSumY = sums[i]
                }
            }
            maxYSum[q.y] = maxSumY
        }
        maxSums[q.index] = max(maxSumX, maxSumY)
    }

    return maxSums
}

func max(a, b int) int {
    if a > b {
        return a
    }
    return b
}
