class Solution {
    fun maximumSumQueries(nums1: IntArray, nums2: IntArray, queries: Array<IntArray>): IntArray {
        val n = nums1.size
        val sumPairs = Array(n) { i -> Triple(nums1[i], nums2[i], nums1[i] + nums2[i]) }
        sumPairs.sortWith(compareBy({ it.first }, { it.second }))

        val maxSums = IntArray(n)
        var currentMax = Int.MIN_VALUE
        for (i in n - 1 downTo 0) {
            currentMax = maxOf(currentMax, sumPairs[i].third)
            maxSums[i] = currentMax
        }

        return queries.map { query ->
            val (xi, yi) = query
            var left = 0
            var right = n - 1
            var idx = -1
            while (left <= right) {
                val mid = left + (right - left) / 2
                if (sumPairs[mid].first >= xi && sumPairs[mid].second >= yi) {
                    idx = mid
                    right = mid - 1
                } else {
                    left = mid + 1
                }
            }
            if (idx == -1) -1 else maxSums[idx]
        }.toIntArray()
    }
}
