class Solution {
    fun maximumSumQueries(nums1: IntArray, nums2: IntArray, queries: Array<IntArray>): IntArray {
        val sortedIndices = (nums1.indices).sortedWith(compareBy({ nums1[it] }, { nums2[it] }))
        val sortedSums = sortedIndices.map { nums1[it] + nums2[it] }.toIntArray()
        val maxSums = sortedSums.clone()
        for (i in maxSums.size - 2 downTo 0) {
            maxSums[i] = maxOf(maxSums[i], maxSums[i + 1])
        }

        return queries.map { query ->
            var result = -1
            val (x, y) = query
            var low = 0
            var high = sortedIndices.size - 1
            while (low <= high) {
                val mid = (low + high) / 2
                if (nums1[sortedIndices[mid]] >= x && nums2[sortedIndices[mid]] >= y) {
                    result = maxSums[mid]
                    high = mid - 1
                } else {
                    low = mid + 1
                }
            }
            result
        }.toIntArray()
    }
}
