#include <vector>
#include <string>
#include <algorithm>
#include <limits.h>

using namespace std;

class Solution {
public:
    int minimumChanges(string s, int k) {
        int n = s.length();
        vector<vector<int>> cost(n, vector<int>(n, 0));
        // Precompute the cost to make substrings semi-palindromes
        for (int len = 2; len <= n; ++len) {
            for (int start = 0; start <= n - len; ++start) {
                int end = start + len - 1;
                cost[start][end] = (s[start] != s[end]) + cost[start + 1][end - 1];
            }
        }

        // dp[i][j] = minimum cost to partition s[0..j] into i parts
        vector<vector<int>> dp(k + 1, vector<int>(n, INT_MAX));
        for (int i = 0; i < n; ++i) {
            dp[1][i] = cost[0][i]; // Base case: partitioning into 1 part
        }

        for (int i = 2; i <= k; ++i) { // Number of partitions
            for (int j = i - 1; j < n; ++j) { // End of the current partition
                for (int m = i - 2; m < j; ++m) { // End of the previous partition
                    if (dp[i - 1][m] != INT_MAX) {
                        dp[i][j] = min(dp[i][j], dp[i - 1][m] + cost[m + 1][j]);
                    }
                }
            }
        }

        return dp[k][n - 1];
    }
};
