#include <vector>
#include <string>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minimumChanges(string s, int k) {
        const int n = s.size();
        vector<vector<int>> dp(k, vector<int>(n, 1e9));
        vector<vector<int>> change(n, vector<int>(n, 0));

        for (int i = 0; i < n; ++i) {
            for (int j = i; j < n; ++j) {
                for (int l = i, r = j; l < r; ++l, --r) {
                    change[i][j] += s[l] != s[r];
                }
            }
        }

        for (int i = 0; i < n; ++i) {
            dp[0][i] = change[0][i];
        }

        for (int i = 1; i < k; ++i) {
            for (int j = 0; j < n; ++j) {
                for (int l = 0; l < j; ++l) {
                    dp[i][j] = min(dp[i][j], dp[i - 1][l] + change[l + 1][j]);
                }
            }
        }

        return dp[k - 1][n - 1];
    }
};
