#include <string.h>
#include <limits.h>

int min(int a, int b) {
    return a < b ? a : b;
}

int countChangesForSemiPalindrome(char *s, int start, int end) {
    int changes = 0;
    while (start < end) {
        if (s[start] != s[end]) changes++;
        start++;
        end--;
    }
    return changes;
}

int minimumChanges(char *s, int k) {
    int len = strlen(s);
    int dp[len+1][k+1];
    memset(dp, 0x3f, sizeof(dp)); // Initialize with a large value.
    dp[0][0] = 0; // Base case: 0 changes for 0 characters and 0 parts.

    for (int i = 1; i <= len; ++i) {
        for (int j = 1; j <= min(i, k); ++j) {
            for (int p = i; p >= j; --p) { // Partition the string.
                dp[i][j] = min(dp[i][j], dp[p-1][j-1] + countChangesForSemiPalindrome(s, p-1, i-1));
            }
        }
    }

    return dp[len][k];
}
