#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int minimumChanges(char *s, int k) {
    int n = strlen(s);
    int dp[201][201] = {0}, cost[201][201] = {0};

    // Precompute the cost of making substrings semi-palindrome
    for (int len = 2; len <= n; ++len) {
        for (int i = 0; i + len <= n; ++i) {
            int j = i + len - 1;
            cost[i][j] = cost[i + 1][j - 1] + (s[i] != s[j]);
        }
    }

    // Initialize dp table
    for (int i = 0; i < n; ++i) {
        dp[i][1] = cost[0][i];
    }

    // Fill dp table
    for (int l = 2; l <= k; ++l) {
        for (int i = l - 1; i < n; ++i) {
            dp[i][l] = 1e9;
            for (int j = l - 2; j < i; ++j) {
                dp[i][l] = fmin(dp[i][l], dp[j][l - 1] + cost[j + 1][i]);
            }
        }
    }

    return dp[n - 1][k];
}
