class Solution {
    fun minimumChanges(s: String, k: Int): Int {
        val n = s.length
        val dp = Array(k + 1) { IntArray(n + 1) { Int.MAX_VALUE / 2 } }
        val cost = Array(n) { IntArray(n) }

        for (len in 1..n) {
            for (i in 0..n - len) {
                val j = i + len - 1
                cost[i][j] = if (len == 1) 0 else cost[i + 1][j - 1] + if (s[i] == s[j]) 0 else 1
            }
        }

        dp[0][0] = 0

        for (i in 1..k) {
            for (j in 1..n) {
                for (l in i - 1 until j) {
                    dp[i][j] = dp[i][j].coerceAtMost(dp[i - 1][l] + cost[l][j - 1])
                }
            }
        }

        return dp[k][n]
    }
}
