class Solution {

    /**
     * @param String $s
     * @param Integer $k
     * @return Integer
     */
    function minimumChanges($s, $k) {
        $n = strlen($s);
        $dp = array_fill(0, $k + 1, array_fill(0, $n + 1, PHP_INT_MAX));
        $dp[0][0] = 0;

        for ($i = 1; $i <= $k; $i++) {
            for ($j = $i; $j <= $n; $j++) {
                for ($l = $i - 1; $l < $j; $l++) {
                    $dp[$i][$j] = min($dp[$i][$j], $dp[$i - 1][$l] + $this->changesToSemiPalindrome(substr($s, $l, $j - $l)));
                }
            }
        }

        return $dp[$k][$n];
    }

    function changesToSemiPalindrome($s) {
        $n = strlen($s);
        $result = PHP_INT_MAX;

        for ($d = 1; $d < $n; $d++) {
            if ($n % $d == 0) {
                $changes = 0;
                for ($i = 0; $i < $d; $i++) {
                    $count = [];
                    for ($j = $i; $j < $n; $j += $d) {
                        if (!isset($count[$s[$j]])) {
                            $count[$s[$j]] = 0;
                        }
                        $count[$s[$j]]++;
                    }
                    $changes += array_sum($count) - max($count);
                }
                $result = min($result, $changes);
            }
        }

        return $result === PHP_INT_MAX ? $n / 2 : $result;
    }
}
