class Solution {

    /**
     * @param String $s
     * @param Integer $k
     * @return Integer
     */
    function minimumChanges($s, $k) {
        $n = strlen($s);
        $dp = array_fill(0, $n + 1, array_fill(0, $k + 1, PHP_INT_MAX));
        $dp[0][0] = 0;

        for ($i = 1; $i <= $n; ++$i) {
            for ($j = 1; $j <= min($i, $k); ++$j) {
                for ($p = $j - 1; $p < $i; ++$p) {
                    $cost = $this->getCost($s, $p, $i - 1);
                    $dp[$i][$j] = min($dp[$i][$j], $dp[$p][$j - 1] + $cost);
                }
            }
        }

        return $dp[$n][$k];
    }

    private function getCost($s, $start, $end) {
        $cost = 0;
        while ($start < $end) {
            if ($s[$start] != $s[$end]) {
                ++$cost;
            }
            ++$start;
            --$end;
        }
        return $cost;
    }
}
