class Solution:
    def minimumChanges(self, s: str, k: int) -> int:
        n = len(s)

        # Precompute the cost of making substrings semi-palindromes
        cost = [[0] * n for _ in range(n)]
        for length in range(2, n + 1):
            for start in range(n - length + 1):
                end = start + length - 1
                cost[start][end] = cost[start + 1][end - 1] + (s[start] != s[end])

        # DP array to store minimum changes required for each substring with different k values
        dp = [[float('inf')] * (k + 1) for _ in range(n + 1)]
        for i in range(n + 1):
            dp[i][1] = cost[0][i - 1]

        for i in range(1, n + 1):
            for j in range(2, min(k, i) + 1):
                for x in range(j - 1, i):
                    dp[i][j] = min(dp[i][j], dp[x][j - 1] + cost[x][i - 1])

        return dp[n][k]
