class Solution:
    def minimumChanges(self, s: str, k: int) -> int:
        def semi_palindrome_cost(s):
            # Function to calculate cost to convert s to a semi-palindrome
            n = len(s)
            if n < 2: return 0  # A single character is already a semi-palindrome
            cost = 0
            for i in range(n // 2):
                if s[i] != s[n - i - 1]:
                    cost += 1
            return cost

        n = len(s)
        if k == 1: return semi_palindrome_cost(s)

        # dp[i][j] represents the minimum change to make the first i characters into j semi-palindromes
        dp = [[float('inf')] * (k + 1) for _ in range(n + 1)]
        dp[0][0] = 0  # Base case

        for i in range(1, n + 1):
            for j in range(1, min(i, k) + 1):
                if j == 1:
                    dp[i][j] = semi_palindrome_cost(s[:i])
                else:
                    for x in range(j - 1, i):
                        dp[i][j] = min(dp[i][j], dp[x][j - 1] + semi_palindrome_cost(s[x:i]))

        return dp[n][k]
