class Solution:
    def minimumChanges(self, s: str, k: int) -> int:
        n = len(s)

        # Function to calculate changes needed to make a substring a semi-palindrome
        def changes_to_make_semi_palindrome(sub):
            m = len(sub)
            if m <= 1: return 0
            changes = 0
            for i in range(m // 2):
                if sub[i] != sub[m - i - 1]:
                    changes += 1
            return changes

        # dp[i][j] = minimum changes needed for first i characters and j partitions
        dp = [[float('inf')] * (k + 1) for _ in range(n + 1)]
        dp[0][0] = 0  # No changes needed for empty string

        for i in range(1, n + 1):
            for j in range(1, min(i, k) + 1):
                if j == 1:
                    dp[i][j] = changes_to_make_semi_palindrome(s[:i])
                else:
                    for x in range(j - 1, i):
                        dp[i][j] = min(dp[i][j], dp[x][j - 1] + changes_to_make_semi_palindrome(s[x:i]))

        return dp[n][k]
