object Solution {
  def minimumChanges(s: String, k: Int): Int = {
    val n = s.length
    val dp = Array.ofDim[Int](n + 1, k + 1, n / 2 + 1)
    for (i <- 0 to n; j <- 0 to k; l <- 0 to n / 2) dp(i)(j)(l) = 1000
    dp(0)(0)(0) = 0

    for (i <- 1 to n; j <- 1 to k; l <- 1 to (i min n / 2)) {
      val half = l min (i - l)
      var changes = 0
      for (m <- 1 to half) {
        if (s(i - m) != s(i - l - m)) changes += 1
        dp(i)(j)(l) = dp(i)(j)(l) min (dp(i - l)(j - 1)(l) + changes)
      }
      dp(i)(j)(0) = dp(i)(j)(0) min dp(i)(j)(l)
    }

    (0 to n / 2).map(l => dp(n)(k)(l)).min
  }
}
