object Solution {
    def minimumChanges(s: String, k: Int): Int = {
        val n = s.length
        val dp = Array.fill(n + 1, k + 1)(Int.MaxValue / 2)
        dp(0)(0) = 0

        for {
            i <- 1 to n
            j <- 1 to k.min(i)
            x <- 1 to i
        } {
            val substr = s.substring(i - x, i)
            val changes = (0 until substr.length / 2).count(l => substr(l) != substr(substr.length - l - 1))
            if (i >= x && j > 0 && dp(i - x)(j - 1) != Int.MaxValue / 2) {
                dp(i)(j) = dp(i)(j) min (dp(i - x)(j - 1) + changes)
            }
        }

        dp(n)(k)
    }
}
