function minimumChanges(s: string, k: number): number {
    const n = s.length;
    const dp = Array.from({ length: n + 1 }, () => Array(k + 1).fill(Infinity));
    const cost = Array.from({ length: n }, () => Array(n).fill(0));

    // Precompute the cost of making substrings semi-palindromes
    for (let i = 0; i < n; i++) {
        for (let j = i; j < n; j++) {
            let changes = 0;
            for (let l = 0; l < (j - i + 1) / 2; l++) {
                if (s[i + l] !== s[j - l]) changes++;
            }
            cost[i][j] = changes;
        }
    }

    dp[0][0] = 0; // Base case

    for (let i = 1; i <= n; i++) {
        for (let j = 1; j <= Math.min(i, k); j++) {
            if (j == 1) {
                dp[i][j] = cost[0][i - 1]; // If only one partition is allowed, use the precomputed cost
            } else {
                for (let l = j - 1; l < i; l++) {
                    dp[i][j] = Math.min(dp[i][j], dp[l][j - 1] + cost[l][i - 1]);
                }
            }
        }
    }

    return dp[n][k];
}
