public class Solution {
    public long MinimumCost(string source, string target, string[] original, string[] changed, int[] cost) {
        int n = source.Length;
        long[,] dp = new long[n + 1, n + 1];
        const long INF = long.MaxValue / 2;

        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                dp[i, j] = INF;
            }
        }

        dp[0, 0] = 0;

        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= i; j++) {
                if (dp[i, j] == INF) continue;
                // No operation, move to next if characters are equal
                if (source[i] == target[j]) {
                    dp[i + 1, j + 1] = Math.Min(dp[i + 1, j + 1], dp[i, j]);
                }
                // Try all operations
                for (int k = 0; k < original.Length; k++) {
                    int len = original[k].Length;
                    if (i + len <= n && j + len <= n && source.Substring(i, len) == original[k] && target.Substring(j, len) == changed[k]) {
                        dp[i + len, j + len] = Math.Min(dp[i + len, j + len], dp[i, j] + cost[k]);
                    }
                }
                // Allow skipping characters in target
                dp[i, j + 1] = Math.Min(dp[i, j + 1], dp[i, j]);
            }
        }

        return dp[n, n] == INF ? -1 : dp[n, n];
    }
}
