public class Solution {
    public long MinimumCost(string source, string target, string[] original, string[] changed, int[] cost) {
        int n = source.Length, m = original.Length;
        var dp = new long[n + 1, n + 1];
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                dp[i, j] = long.MaxValue;
            }
        }
        dp[0, 0] = 0; // Cost to convert source[0..0) to target[0..0) is 0

        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                if (dp[i, j] == long.MaxValue) continue;

                // Skip a character in source
                if (i < n) dp[i + 1, j] = Math.Min(dp[i + 1, j], dp[i, j]);

                // Skip a character in target
                if (j < n) dp[i, j + 1] = Math.Min(dp[i, j + 1], dp[i, j]);

                for (int k = 0; k < m; k++) {
                    int len = original[k].Length;
                    // Replace a substring in source
                    if (i + len <= n && j + len <= n && source.Substring(i, len) == original[k] && target.Substring(j, len) == changed[k]) {
                        dp[i + len, j + len] = Math.Min(dp[i + len, j + len], dp[i, j] + cost[k]);
                    }
                }
            }
        }

        return dp[n, n] == long.MaxValue ? -1 : dp[n, n];
    }
}
