public class Solution {
    public long MinimumCost(string source, string target, string[] original, string[] changed, int[] cost) {
        if (source == target) return 0;

        int n = source.Length;
        var dp = new long[n + 1];
        for (int i = 0; i <= n; i++) dp[i] = long.MaxValue;
        dp[0] = 0;

        for (int i = 0; i < n; i++) {
            if (dp[i] == long.MaxValue) continue;
            for (int j = 0; j < original.Length; j++) {
                string ori = original[j], cha = changed[j];
                int oriLen = ori.Length;
                if (i + oriLen <= n && source.Substring(i, oriLen) == ori) {
                    int idx = source.IndexOf(ori, i);
                    while (idx != -1 && idx + oriLen <= n) {
                        string tempSource = source.Substring(0, idx) + cha + source.Substring(idx + oriLen);
                        int tempIdx = tempSource.IndexOf(target.Substring(0, i + oriLen), 0);
                        if (tempIdx == 0) {
                            dp[i + oriLen] = Math.Min(dp[i + oriLen], dp[i] + cost[j]);
                        }
                        if (idx + oriLen < n) {
                            idx = source.IndexOf(ori, idx + 1);
                        } else break;
                    }
                }
            }
        }

        return dp[n] == long.MaxValue ? -1 : dp[n];
    }
}
