#include <vector>
#include <string>
#include <unordered_map>
#include <algorithm>

using namespace std;

class Solution {
public:
    long long minimumCost(string source, string target, vector<string>& original, vector<string>& changed, vector<int>& cost) {
        int n = source.size();
        vector<vector<long long>> dp(n + 1, vector<long long>(n + 1, INT64_MAX));
        unordered_map<string, vector<pair<string, int>>> transformations;

        // Initialize transformations map
        for (int i = 0; i < original.size(); ++i) {
            transformations[original[i]].emplace_back(changed[i], cost[i]);
        }

        // Base case: cost of converting an empty string to an empty string is 0
        dp[0][0] = 0;

        for (int i = 0; i <= n; ++i) {
            for (int j = 0; j <= n; ++j) {
                if (i > 0 && j > 0 && source[i - 1] == target[j - 1]) {
                    // If the characters are the same, no cost is added
                    dp[i][j] = min(dp[i][j], dp[i - 1][j - 1]);
                }
                if (i > 0) {
                    // Skip a character in the source string
                    dp[i][j] = min(dp[i][j], dp[i - 1][j]);
                }
                if (j > 0) {
                    // Skip a character in the target string, not a valid operation but keeps the array populated
                    dp[i][j] = min(dp[i][j], dp[i][j - 1]);
                }
                // Attempt transformations
                for (int k = 1; k <= i; ++k) {
                    string subSource = source.substr(i - k, k);
                    if (transformations.find(subSource) != transformations.end()) {
                        for (auto& [changedStr, cost] : transformations[subSource]) {
                            int len = changedStr.size();
                            if (j >= len && target.substr(j - len, len) == changedStr) {
                                dp[i][j] = min(dp[i][j], dp[i - k][j - len] + cost);
                            }
                        }
                    }
                }
            }
        }

        return dp[n][n] == INT64_MAX ? -1 : dp[n][n];
    }
};
