#include <vector>
#include <string>
#include <unordered_map>
#include <algorithm>

using namespace std;

class Solution {
public:
    long long minimumCost(string source, string target, vector<string>& original, vector<string>& changed, vector<int>& cost) {
        int n = source.size();
        vector<vector<long long>> dp(n + 1, vector<long long>(n + 1, LLONG_MAX));
        unordered_map<string, vector<pair<string, int>>> mp;

        for (int i = 0; i < original.size(); i++) {
            mp[original[i]].push_back({changed[i], cost[i]});
        }

        dp[0][0] = 0; // cost to convert empty source to empty target

        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                if (i > 0) dp[i][j] = min(dp[i][j], dp[i-1][j]); // delete character from source
                if (j > 0) dp[i][j] = min(dp[i][j], dp[i][j-1]); // add character to target
                for (int k = 1; k <= i && k <= j; k++) { // replace substring of length k
                    string sub = source.substr(i-k, k);
                    if (mp.find(sub) != mp.end()) {
                        for (auto &p : mp[sub]) {
                            string to = p.first;
                            int cst = p.second;
                            if (to.size() <= j && target.substr(j-to.size(), to.size()) == to) {
                                dp[i][j] = min(dp[i][j], dp[i-k][j-to.size()] + cst);
                            }
                        }
                    }
                }
            }
        }

        return dp[n][n] == LLONG_MAX ? -1 : dp[n][n];
    }
};
