#include <string.h>
#include <limits.h>

long long minimumCost(char* source, char* target, char** original, int originalSize, char** changed, int changedSize, int* cost, int costSize) {
    int n = strlen(source);
    long long dp[n+1];
    for (int i = 0; i <= n; i++) {
        dp[i] = LLONG_MAX;
    }
    dp[0] = 0;

    for (int i = 0; i < n; i++) {
        if (dp[i] == LLONG_MAX) continue;
        for (int j = 0; j < originalSize; j++) {
            int len = strlen(original[j]);
            if (i + len > n) continue;
            if (strncmp(&source[i], original[j], len) == 0 && strncmp(&target[i], changed[j], len) == 0) {
                dp[i + len] = dp[i + len] < dp[i] + cost[j] ? dp[i + len] : dp[i] + cost[j];
            }
        }
        // Handling the case where source[i] matches target[i] without any operation.
        if (source[i] == target[i]) {
            dp[i+1] = dp[i+1] < dp[i] ? dp[i+1] : dp[i];
        }
    }

    return dp[n] == LLONG_MAX ? -1 : dp[n];
}
