#include <string.h>
#include <stdlib.h>

#define MAX(a,b) ((a)>(b)?(a):(b))
#define MIN(a,b) ((a)<(b)?(a):(b))

long long minimumCost(char* source, char* target, char** original, int originalSize, char** changed, int changedSize, int* cost, int costSize) {
    int n = strlen(source);
    long long dp[n+1];
    for (int i = 0; i <= n; i++) dp[i] = LLONG_MAX;
    dp[0] = 0;

    for (int i = 0; i < n; i++) {
        if (dp[i] == LLONG_MAX) continue;

        for (int j = 0; j < originalSize; j++) {
            int len = strlen(original[j]);
            if (i + len > n || strncmp(source + i, original[j], len) != 0) continue;

            int k = i;
            while (k < i + len) {
                if (source[k] != original[j][k - i]) break;
                if (target[k] != changed[j][k - i]) break;
                k++;
            }

            if (k == i + len) dp[i+len] = MIN(dp[i+len], dp[i] + cost[j]);
        }

        // Direct copy without cost if next character matches
        if (source[i] == target[i]) dp[i+1] = MIN(dp[i+1], dp[i]);
    }

    return dp[n] == LLONG_MAX ? -1 : dp[n];
}
