#include <string.h>
#include <limits.h>

long long dp[1001];

long long minimumCost(char* source, char* target, char** original, int originalSize, char** changed, int changedSize, int* cost, int costSize) {
    int n = strlen(source);
    for (int i = 0; i <= n; i++) dp[i] = LLONG_MAX;
    dp[0] = 0; // Cost to convert an empty prefix

    for (int i = 0; i < n; i++) {
        if (source[i] == target[i]) { // No cost to change if characters are already the same
            if (dp[i] < LLONG_MAX) {
                dp[i+1] = dp[i];
            }
        }
        for (int j = 0; j < originalSize; j++) {
            int len = strlen(original[j]);
            if (i + len <= n && strncmp(source+i, original[j], len) == 0 && strncmp(target+i, changed[j], len) == 0) {
                if (dp[i] < LLONG_MAX) {
                    dp[i + len] = dp[i + len] > dp[i] + cost[j] ? dp[i] + cost[j] : dp[i + len];
                }
            }
        }
    }

    return dp[n] == LLONG_MAX ? -1 : dp[n];
}
