import java.util.HashMap;
import java.util.Arrays;

class Solution {
    public long minimumCost(String source, String target, String[] original, String[] changed, int[] cost) {
        if (source.equals(target)) return 0;

        int n = source.length();
        long[][] dp = new long[n + 1][n + 1];
        for (long[] row : dp) Arrays.fill(row, Long.MAX_VALUE);
        dp[0][0] = 0; // Cost of transforming 0 characters to match is 0.

        // Mapping transformation costs
        HashMap<String, Integer> map = new HashMap<>();
        for (int i = 0; i < original.length; ++i) {
            String key = original[i] + "->" + changed[i];
            if (!map.containsKey(key) || map.get(key) > cost[i]) {
                map.put(key, cost[i]);
            }
        }

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j <= i; ++j) {
                if (dp[i][j] == Long.MAX_VALUE) continue;
                // Match next character without transformation
                if (j < n && source.charAt(i) == target.charAt(j)) {
                    dp[i + 1][j + 1] = Math.min(dp[i + 1][j + 1], dp[i][j]);
                }
                // Try all possible transformations
                for (String key : map.keySet()) {
                    String[] parts = key.split("->");
                    String orig = parts[0];
                    String chng = parts[1];
                    int origLen = orig.length();
                    int chngLen = chng.length();
                    if (i + origLen <= n && j + chngLen <= n && source.startsWith(orig, i) && target.startsWith(chng, j)) {
                        dp[i + origLen][j + chngLen] = Math.min(dp[i + origLen][j + chngLen], dp[i][j] + map.get(key));
                    }
                }
            }
        }

        long ans = Long.MAX_VALUE;
        for (int i = 0; i <= n; ++i) {
            ans = Math.min(ans, dp[n][i]);
        }

        return ans == Long.MAX_VALUE ? -1 : ans;
    }
}
