var minimumCost = function(source, target, original, changed, cost) {
    const m = original.length;
    const dp = Array(source.length + 1).fill(0);
    for (let i = 1; i <= source.length; i++) dp[i] = dp[i - 1] + 1e9;
    const costMap = new Map();
    for (let i = 0; i < m; i++) {
        if (!costMap.has(original[i])) costMap.set(original[i], []);
        costMap.get(original[i]).push({ changed: changed[i], cost: cost[i] });
    }
    for (let i = 0; i < source.length; i++) {
        for (let j = i + 1; j <= source.length; j++) {
            const sub = source.slice(i, j);
            if (costMap.has(sub)) {
                for (const { changed, cost } of costMap.get(sub)) {
                    let k = 0;
                    for (; k < changed.length && i + k < target.length; k++) {
                        if (target[i + k] !== changed[k]) break;
                    }
                    if (k === changed.length && dp[i] + cost < dp[j]) dp[j] = dp[i] + cost;
                }
            }
        }
    }
    return dp[source.length] >= 1e9 ? -1 : dp[source.length];
};
