class Solution {
    fun minimumCost(source: String, target: String, original: Array<String>, changed: Array<String>, cost: IntArray): Long {
        if (source == target) return 0

        val map = mutableMapOf<String, MutableList<Pair<String, Int>>>()

        for (i in original.indices) {
            map.getOrPut(original[i]) { mutableListOf() }.add(Pair(changed[i], cost[i]))
        }

        val dp = Array(source.length + 1) { LongArray(target.length + 1) { Long.MAX_VALUE } }
        dp[0][0] = 0

        for (i in 0..source.length) {
            for (j in 0..target.length) {
                if (i < source.length && j < target.length && source[i] == target[j]) {
                    dp[i + 1][j + 1] = dp[i + 1][j + 1].coerceAtMost(dp[i][j])
                }
                if (i < source.length) {
                    for ((chg, cost) in map.getOrDefault(source.substring(i, i + 1), mutableListOf())) {
                        if (j + chg.length <= target.length && target.substring(j, j + chg.length) == chg) {
                            dp[i + 1][j + chg.length] = dp[i + 1][j + chg.length].coerceAtMost(dp[i][j] + cost)
                        }
                    }
                }
            }
        }

        return if (dp[source.length][target.length] == Long.MAX_VALUE) -1 else dp[source.length][target.length]
    }
}
