class Solution {

    /**
     * @param String $source
     * @param String $target
     * @param String[] $original
     * @param String[] $changed
     * @param Integer[] $cost
     * @return Integer
     */
    function minimumCost($source, $target, $original, $changed, $cost) {
        $n = strlen($source);
        $dp = array_fill(0, $n + 1, PHP_INT_MAX);
        $dp[0] = 0;

        for ($i = 0; $i < $n; $i++) {
            if ($dp[$i] == PHP_INT_MAX) continue;
            foreach ($original as $idx => $orig) {
                $len = strlen($orig);
                $sub = substr($source, $i, $len);
                if ($sub == $orig && substr($target, $i, $len) == $changed[$idx]) {
                    $dp[$i + $len] = min($dp[$i + $len], $dp[$i] + $cost[$idx]);
                }
            }
        }

        return $dp[$n] == PHP_INT_MAX ? -1 : $dp[$n];
    }
}
