class Solution {

    /**
     * @param String $source
     * @param String $target
     * @param String[] $original
     * @param String[] $changed
     * @param Integer[] $cost
     * @return Integer
     */
    function minimumCost($source, $target, $original, $changed, $cost) {
        $n = strlen($source);
        $m = count($original);
        $dp = array_fill(0, $n + 1, PHP_INT_MAX);
        $dp[0] = 0;

        for ($i = 0; $i < $n; $i++) {
            if ($dp[$i] == PHP_INT_MAX) continue;
            for ($j = 0; $j < $m; $j++) {
                $orig = $original[$j];
                $chg = $changed[$j];
                $cst = $cost[$j];
                $len = strlen($orig);
                if ($i + $len <= $n && substr($source, $i, $len) === $orig) {
                    for ($k = 1; $k <= $len; $k++) {
                        $subOrig = substr($orig, 0, $k);
                        $subChg = substr($chg, 0, $k);
                        if ($i + $k <= $n && substr($target, $i, $k) === $subChg) {
                            $dp[$i + $k] = min($dp[$i + $k], $dp[$i] + $cst);
                        }
                    }
                }
            }
        }

        return $dp[$n] === PHP_INT_MAX ? -1 : $dp[$n];
    }
}
