from typing import List

class Solution:
    def minimumCost(self, source: str, target: str, original: List[str], changed: List[str], cost: List[int]) -> int:
        # Dynamic programming approach to find the minimum cost
        n = len(source)
        m = len(original)
        dp = [[float('inf')] * (n + 1) for _ in range(n + 1)]
        for i in range(n + 1):
            dp[i][i] = 0  # No cost to convert a string to itself

        # Preprocess the costs for direct replacements
        direct_cost = {}
        for i in range(m):
            if original[i] not in direct_cost or cost[i] < direct_cost[original[i]][1]:
                direct_cost[original[i]] = (changed[i], cost[i])

        for length in range(1, n + 1):
            for start in range(n - length + 1):
                end = start + length
                # Check if direct replacement is possible and cheaper
                for i in range(m):
                    if source[start:end] == original[i] and target[start:start+len(changed[i])] == changed[i] and len(source[start:end]) == len(original[i]):
                        dp[start][end] = min(dp[start][end], dp[start][start] + cost[i])
                # Try splitting the interval into two
                for mid in range(start + 1, end):
                    dp[start][end] = min(dp[start][end], dp[start][mid] + dp[mid][end])

        # Convert the entire source to target
        result = dp[0][n]
        return -1 if result == float('inf') else result
