object Solution {
    def minimumCost(source: String, target: String, original: Array[String], changed: Array[String], cost: Array[Int]): Long = {
        val n = source.length
        val m = original.length
        val dp = Array.fill(n + 1)(Long.MaxValue / 2)
        dp(0) = 0

        for (i <- 1 to n) {
            if (source(i - 1) == target(i - 1)) dp(i) = dp(i - 1)
            for (j <- 0 until m) {
                if (original(j).length <= i) {
                    val startIdx = i - original(j).length
                    if (source.substring(startIdx, i) == original(j) && target.substring(startIdx, i) == changed(j)) {
                        dp(i) = Math.min(dp(i), dp(startIdx) + cost(j))
                    }
                }
            }
        }

        if (dp(n) >= Long.MaxValue / 2) -1 else dp(n)
    }
}
