object Solution {
    def minimumCost(source: String, target: String, original: Array[String], changed: Array[String], cost: Array[Int]): Long = {
        val n = source.length
        val operations = original.zip(changed).zip(cost).map { case ((o, c), co) => (o, c, co) }

        // Dynamic programming table to store the minimum cost to convert the first i characters of source to target
        val dp = Array.fill(n + 1)(Long.MaxValue)
        dp(0) = 0 // Cost to convert 0 characters is 0

        for (i <- 1 to n) {
            // Try converting source to target directly character by character if they are different
            if (source(i - 1) == target(i - 1)) dp(i) = dp(i - 1) // No cost if characters are the same

            // Try using operations to convert source to target
            for ((o, c, co) <- operations) {
                if (i >= o.length) {
                    val startIndex = i - o.length
                    // Check if the operation can be applied: the substring matches and the target can be achieved
                    if (source.substring(startIndex, i) == o && target.substring(startIndex, i) == c) {
                        // Update the cost if this operation provides a lower cost solution
                        dp(i) = Math.min(dp(i), dp(startIndex) + co)
                    }
                }
            }
        }

        if (dp(n) == Long.MaxValue) -1 else dp(n)
    }
}
