class Solution {
    func minimumCost(_ source: String, _ target: String, _ original: [String], _ changed: [String], _ cost: [Int]) -> Int {
        let n = source.count
        var dp = Array(repeating: Int.max, count: n + 1)
        dp[0] = 0

        let s = Array(source), t = Array(target)

        for i in 1...n {
            dp[i] = dp[i-1] + (s[i-1] == t[i-1] ? 0 : Int.max / 2)
            for j in 0..<original.count {
                if i >= original[j].count {
                    let substr = String(s[i-original[j].count..<i])
                    if substr == original[j] {
                        let index = s[0..<i-original[j].count].lastIndex(of: t[i-1])
                        if index != nil || changed[j].last == t[i-1] {
                            let k = index != nil ? s.distance(from: s.startIndex, to: index!) + 1 : i - original[j].count
                            dp[i] = min(dp[i], dp[k] + cost[j])
                        }
                    }
                }
            }
        }

        var minCost = Int.max
        for i in 0...n {
            if String(t[0..<i]) == String(s[0..<i]) {
                minCost = min(minCost, dp[i])
            }
        }

        return minCost == Int.max ? -1 : minCost
    }
}
