function minimumCost(source: string, target: string, original: string[], changed: string[], cost: number[]): number {
    const n = source.length;
    const dp: number[] = new Array(n + 1).fill(Infinity);
    dp[0] = 0;

    for (let i = 0; i < n; i++) {
        if (dp[i] !== Infinity) {
            for (let j = 0; j < original.length; j++) {
                if (source.startsWith(original[j], i) && i + original[j].length <= n) {
                    const newTarget = source.substring(0, i) + changed[j] + source.substring(i + original[j].length);
                    if (target.startsWith(newTarget.substring(0, i + changed[j].length))) {
                        dp[i + original[j].length] = Math.min(dp[i + original[j].length], dp[i] + cost[j]);
                    }
                }
            }
        }
    }

    return dp[n] === Infinity ? -1 : dp[n];
}
