function minimumCost(source: string, target: string, original: string[], changed: string[], cost: number[]): number {
    // Initialize dp array to store the minimum cost to convert source[0..i] to target[0..i]
    let dp: number[] = new Array(source.length + 1).fill(Infinity);
    dp[0] = 0; // Base case: cost of converting empty string to empty string is 0

    // Preprocessing: Create a map for faster lookup of conversion options and costs
    let conversionMap: Map<string, {target: string, cost: number}[]> = new Map();
    for (let i = 0; i < original.length; i++) {
        if (!conversionMap.has(original[i])) {
            conversionMap.set(original[i], []);
        }
        conversionMap.get(original[i])!.push({target: changed[i], cost: cost[i]});
    }

    // Dynamic programming to find minimum cost
    for (let i = 0; i < source.length; i++) {
        if (dp[i] === Infinity) continue; // Skip if no valid conversion found so far
        for (let j = i + 1; j <= source.length; j++) {
            let substr = source.slice(i, j);
            if (conversionMap.has(substr)) {
                for (let conversion of conversionMap.get(substr)!) {
                    let nextIndex = i + conversion.target.length;
                    // Ensure the converted string aligns with the target up to the next index
                    if (target.startsWith(source.substring(0, i) + conversion.target, 0)) {
                        dp[nextIndex] = Math.min(dp[nextIndex], dp[i] + conversion.cost);
                    }
                }
            }
        }
    }

    // Check if conversion is possible
    if (dp[source.length] === Infinity || source === target) {
        return source === target ? 0 : -1; // If source is already equal to target, cost is 0; otherwise, -1 if conversion is impossible
    }

    // Attempt direct conversions that may lead to the target string without intermediate steps
    for (let i = 0; i < original.length; i++) {
        if (source === original[i] && target === changed[i]) {
            return Math.min(dp[source.length], cost[i]); // Return the lesser of the DP result or direct conversion cost
        }
    }

    return dp[source.length]; // Return the minimum cost to convert source to target
}
