function minimumCost(source: string, target: string, original: string[], changed: string[], cost: number[]): number {
    const m = original.length;
    const n = source.length;
    const dp = Array(n + 1).fill(Infinity);
    dp[0] = 0; // Cost of converting an empty string to an empty string is 0.

    for (let i = 0; i < n; i++) {
        if (dp[i] !== Infinity) {
            for (let j = 0; j < m; j++) {
                const orig = original[j];
                const chng = changed[j];
                const cst = cost[j];
                if (i + orig.length <= n && source.substr(i, orig.length) === orig) {
                    let k = i + orig.length;
                    let tempTarget = source.substring(0, i) + chng + source.substring(k);
                    if (target.startsWith(tempTarget.substring(0, k))) {
                        dp[k] = Math.min(dp[k], dp[i] + cst);
                    }
                }
            }
        }
    }

    // Final check if target can be formed and calculate minimum cost
    if (dp[n] === Infinity || source === target && dp[n] > 0) {
        return -1; // If it's impossible to form target from source or no operations needed but cost is positive
    } else {
        return dp[n];
    }
}
