public class Solution {
    public int MinOperations(IList<int> nums, int target) {
        long sum = 0;
        var freq = new Dictionary<int, int>();
        foreach (var num in nums) {
            sum += num;
            int log = Log2(num);
            if (!freq.ContainsKey(log)) {
                freq[log] = 1;
            } else {
                freq[log]++;
            }
        }

        if (sum < target) return -1;
        if (sum == target) return 0;

        int operations = 0;
        for (int i = 30; i >= 0 && target > 0; --i) {
            int pow2 = 1 << i;
            if ((target & pow2) != 0) {
                if (!freq.ContainsKey(i) || freq[i] == 0) {
                    bool found = false;
                    for (int j = i + 1; j <= 30; ++j) {
                        if (freq.ContainsKey(j) && freq[j] > 0) {
                            while (j > i && freq[j] > 0) {
                                freq[j]--;
                                operations++;
                                if (!freq.ContainsKey(j - 1)) freq[j - 1] = 0;
                                freq[j - 1] += 2;
                                j--;
                            }
                            found = true;
                            break;
                        }
                    }
                    if (!found) return -1;
                }
                freq[i]--;
                target -= pow2;
            }
            if (freq.ContainsKey(i) && freq[i] > 1) {
                int toAdd = freq[i] / 2;
                operations += toAdd;
                if (!freq.ContainsKey(i - 1)) freq[i - 1] = 0;
                freq[i - 1] += toAdd * 2;
                freq[i] %= 2;
            }
        }
        return operations;
    }

    private int Log2(int x) {
        int result = 0;
        while (x > 1) {
            result++;
            x >>= 1;
        }
        return result;
    }
}
