#include <vector>
#include <unordered_map>

using namespace std;

class Solution {
public:
    int minOperations(vector<int>& nums, int target) {
        unordered_map<int, int> powerCount;
        int sum = 0, operations = 0;

        // Count the occurrences of each power of 2
        for (int num : nums) {
            powerCount[num]++;
            sum += num;
        }

        if (sum < target) return -1; // Impossible to reach the target

        for (int power = 30; power >= 0; power--) {
            int value = 1 << power;
            while (powerCount[value] > 0 && sum - value >= target) {
                sum -= value;
                powerCount[value]--;
                powerCount[value / 2] += 2;
                operations++;
            }
        }

        return sum == target ? operations : -1;
    }
};
