#include <vector>
#include <algorithm>
#include <unordered_map>

using namespace std;

class Solution {
public:
    int minOperations(vector<int>& nums, int target) {
        unordered_map<int, int> freq;
        long long totalSum = 0;
        for (int num : nums) {
            totalSum += num;
            freq[num]++;
        }

        if (totalSum < target) {
            return -1; // Impossible if total sum is less than target
        }

        int operations = 0;
        while (true) {
            if (freq.count(target)) {
                return operations;
            }

            if (target % 2 == 0 && freq[target / 2] >= 2) {
                return operations + 1;
            }

            if (target == 1) {
                return -1; // Can't split 1 further to make a sum
            }

            if (target % 2 == 1) { // If target is odd
                target--; // Subtract 1 and continue
            } else { // If target is even
                target /= 2; // Halve the target
                operations++; // Increment operations as this is a valid split
            }
        }
    }
};
