#include <stdio.h>

int minOperations(int* nums, int numsSize, int target) {
    long long sum = 0;
    int count[31] = {0};
    for (int i = 0; i < numsSize; i++) {
        sum += nums[i];
        int bit = 0;
        while ((1 << bit) < nums[i]) bit++;
        count[bit]++;
    }
    if (sum < target) return -1;
    int ops = 0;
    for (int bit = 30; bit >= 0; bit--) {
        if (target & (1 << bit)) {
            if (count[bit] > 0) {
                count[bit]--;
            } else {
                int nextBit = bit;
                while (nextBit < 31 && count[nextBit] == 0) nextBit++;
                if (nextBit == 31) return -1;
                while (nextBit > bit) {
                    count[nextBit]--;
                    count[nextBit - 1] += 2;
                    ops++;
                    nextBit--;
                }
                count[bit]--;
            }
        }
        if (bit > 0) {
            count[bit - 1] += count[bit] * 2;
            count[bit] = 0;
        }
    }
    return ops;
}
