class Solution {
    fun minOperations(nums: List<Int>, target: Int): Int {
        var sum = nums.sum()
        val freq = IntArray(32)
        for (num in nums) {
            freq[31 - num.countLeadingZeroBits()]++
        }
        if (sum < target) return -1
        var res = 0
        var t = target
        for (i in 31 downTo 0) {
            if ((t shr i) and 1 == 1) {
                if (freq[i] > 0) {
                    freq[i]--
                } else {
                    var j = i - 1
                    while (j >= 0 && freq[j] == 0) j--
                    if (j < 0) return -1
                    while (j < i) {
                        freq[j] -= 2
                        freq[j + 1]++
                        res++
                        j++
                    }
                    freq[i]--
                }
            }
            if (freq[i] > 0) {
                sum -= freq[i] * (1 shl i)
                if (sum < t) return -1
            }
        }
        return res
    }
}
