class Solution {
    fun minOperations(nums: List<Int>, target: Int): Int {
        var total = 0
        val count = mutableMapOf<Int, Int>()
        for (num in nums) {
            total += num
            count[num] = count.getOrDefault(num, 0) + 1
        }
        if (total < target) return -1
        if (total == target) return 0

        var ops = 0
        var remain = target
        for (i in 30 downTo 0) {
            val value = 1 shl i
            if (remain >= value) {
                val needed = remain / value
                val available = count.getOrDefault(value, 0)
                if (available >= needed) {
                    return ops
                } else {
                    remain -= available * value
                    ops += available
                }
            }
            if (count.containsKey(value) && count[value]!! > 0) {
                val half = value / 2
                if (half > 0) {
                    count[half] = count.getOrDefault(half, 0) + 2 * count[value]!!
                    ops += count[value]!!
                }
            }
        }
        return if (remain > 0) -1 else ops
    }
}
