class Solution {
    fun minOperations(nums: List<Int>, target: Int): Int {
        var ops = 0
        var currentSum = nums.sum()
        val powerCount = IntArray(32)
        nums.forEach { num ->
            var power = 0
            while ((1 shl power) < num) power++
            powerCount[power]++
        }
        if (currentSum < target) return -1
        for (i in 31 downTo 0) {
            val value = 1 shl i
            while (powerCount[i] > 0 && currentSum - value >= target) {
                currentSum -= value
                powerCount[i]--
                ops++
            }
        }
        if (currentSum == target) return ops
        var extraOps = 0
        for (i in 0 until 32) {
            while (powerCount[i] > 0) {
                val value = 1 shl i
                if (currentSum + value <= target) {
                    currentSum += value
                    powerCount[i]--
                    extraOps++
                } else {
                    break
                }
            }
            if (currentSum == target) return ops + extraOps
        }
        return if (currentSum == target) ops else -1
    }
}
