class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $target
     * @return Integer
     */
    function minOperations($nums, $target) {
        $count = 0;
        $sum = 0;
        $pow2 = array_fill(0, 31, 0);

        foreach ($nums as $num) {
            $sum += $num;
            $pow2[(int)log($num, 2)]++;
        }

        if ($sum < $target) return -1;
        if ($sum == $target) return 0;

        for ($i = 30; $i >= 0 && $sum >= $target; $i--) {
            if ($pow2[$i] == 0) continue;
            $value = 1 << $i;
            $maxRemove = min($pow2[$i], (int)(($sum - $target) / $value));
            if ($maxRemove > 0) {
                $sum -= $value * $maxRemove;
                $count += $maxRemove;
                $pow2[$i] -= $maxRemove;
                $pow2[$i - 1] += 2 * $maxRemove;
            }
        }

        return $sum == $target ? $count : -1;
    }
}
