impl Solution {
    pub fn min_operations(nums: Vec<i32>, target: i32) -> i32 {
        let mut sum = 0;
        let mut max_bit = 0;
        let mut bit_count = vec![0; 31];

        for &num in &nums {
            sum += num;
            for i in 0..31 {
                if num == 1 << i {
                    bit_count[i] += 1;
                    max_bit = max_bit.max(i);
                    break;
                }
            }
        }

        if sum < target {
            return -1;
        }

        let mut ops = 0;
        let mut curr_target = target;

        for bit in (0..=max_bit).rev() {
            let val = 1 << bit;
            if curr_target >= val {
                let needed = curr_target / val;
                let take = needed.min(bit_count[bit]);
                curr_target -= take * val;
                ops += take;
            }

            if curr_target == 0 {
                return ops;
            }

            bit_count[bit - 1] += bit_count[bit] * 2;
        }

        if curr_target > 0 {
            -1
        } else {
            ops
        }
    }
}
