impl Solution {
    pub fn min_operations(nums: Vec<i32>, target: i32) -> i32 {
        let mut total = nums.iter().sum::<i32>();
        if total < target {
            return -1;
        }

        let mut operations = 0;
        let mut bits = vec![0; 32];
        for &num in &nums {
            bits[num.trailing_zeros() as usize] += 1;
        }

        let mut remain = target;
        for i in (0..32).rev() {
            if remain == 0 {
                break;
            }
            let val = 1 << i;
            if val <= remain {
                let max_use = remain / val;
                let use_count = max_use.min(bits[i]);
                remain -= use_count * val;
                bits[i] -= use_count;
            }
            if i > 0 && bits[i] > 0 {
                let next_half = bits[i] * 2;
                bits[i - 1] += next_half;
                operations += bits[i];
                bits[i] = 0;
            }
        }

        if remain > 0 {
            -1
        } else {
            operations
        }
    }
}
